function loadStudentsTable(){$.ajax({type:"GET",url:"http://localhost:3000/api/Student",success:a=>{$("#studentsTable").DataTable({data:a,columns:[{data:"participantNo"},{data:"contact.firstName"},{data:"contact.lastName"},{data:"contact.email"},{data:"college",render:a=>a.substring(a.indexOf("#")+1)},{data:"course",render:a=>a.substring(a.indexOf("#")+1)},{data:"currentCredits"},{data:"graduated"}]})}})}function loadCoursesTable(){$.ajax({type:"GET",url:"http://localhost:3000/api/Course",success:a=>{$("#coursesTable").DataTable({data:a,columns:[{data:"courseCode"},{data:"courseTitle"},{data:"courseType"},{data:"qualificationType"},{data:"deliveryMode"},{data:"department"},{data:"NFQLevel"},{data:"noOfSemesters"},{data:"totalCredits"}]})}})}function loadModulesTable(){$.ajax({type:"GET",url:"http://localhost:3000/api/Module",success:a=>{console.log(a),$("#modulesTable").DataTable({data:a,columns:[{data:"crn"},{data:"moduleCode"},{data:"title"},{data:"credits"},{data:"Level"},{data:"deliveries",render:a=>a[0].course.substring(a[0].course.indexOf("#")+1)}]})}})}$(()=>{$("body").is(".admin")&&(loadStudentsTable(),loadCoursesTable(),loadModulesTable())});